import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { WorkExperience } from '../../types';
import Section from '../ui/Section';
import Container from '../ui/Container';
import Heading from '../ui/Heading';
import Timeline from '../ui/Timeline';
import KpiMetric from '../ui/KpiMetric';
import CodeBlock from '../ui/CodeBlock';
import { theme } from '../../styles/theme';

interface ExperienceProps {
  experience: WorkExperience[];
}

const ExperienceWrapper = styled(Section)`
  min-height: 100vh;
  background: linear-gradient(180deg, ${theme.colors.backgroundAlt} 0%, ${theme.colors.background} 100%);
  position: relative;
  overflow: hidden;
  padding: ${theme.spacing.xxl} 0;

  /* Add subtle tech pattern to background */
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: radial-gradient(circle at 25px 25px, rgba(74, 157, 255, 0.1) 2%, transparent 0%),
                      radial-gradient(circle at 75px 75px, rgba(74, 157, 255, 0.05) 2%, transparent 0%);
    background-size: 100px 100px;
    opacity: 0.4;
    z-index: 0;
  }
`;

const ExperienceContainer = styled(Container)`
  position: relative;
  z-index: 1;
`;

const SectionTitle = styled.div`
  margin-bottom: ${theme.spacing.xxl};
  text-align: center;
`;

const ExperienceContent = styled.div`
  display: flex;
  margin-top: ${theme.spacing.xl};
  gap: ${theme.spacing.xxl};

  @media (max-width: ${theme.breakpoints.md}) {
    flex-direction: column;
  }
`;

const TimelineContainer = styled.div`
  flex: 0 0 320px;

  @media (max-width: ${theme.breakpoints.md}) {
    flex: 1;
    margin-bottom: ${theme.spacing.xl};
  }
`;

const DetailContainer = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  background: rgba(20, 25, 35, 0.6);
  backdrop-filter: blur(10px);
  border-radius: ${theme.borderRadius.xl};
  padding: ${theme.spacing.xl} ${theme.spacing.xxl};
  border: 1px solid rgba(74, 157, 255, 0.2);
  box-shadow: 0 15px 35px rgba(0, 0, 0, 0.2),
              inset 0 0 0 1px rgba(255, 255, 255, 0.05);
`;

const JobHeader = styled.div`
  margin-bottom: ${theme.spacing.xl};
  padding-bottom: ${theme.spacing.md};
  display: flex;
  flex-direction: column;
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 1px;
    background: linear-gradient(to right, 
      rgba(74, 157, 255, 0.5), 
      rgba(74, 157, 255, 0.1), 
      transparent
    );
  }
`;

const JobTitle = styled.h3`
  font-size: ${theme.typography.sizes.title1};
  margin-bottom: ${theme.spacing.sm};
  background: linear-gradient(135deg, #fff 20%, ${theme.colors.primary} 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  letter-spacing: -0.5px;
`;

const JobDetails = styled.div`
  font-size: ${theme.typography.sizes.body};
  color: ${theme.colors.textSecondary};
  display: flex;
  align-items: center;
  flex-wrap: wrap;
  gap: ${theme.spacing.md};
  margin-top: ${theme.spacing.sm};
`;

const JobCompany = styled.span`
  font-weight: ${theme.typography.fontWeights.semibold};
  color: ${theme.colors.text};
  font-size: ${theme.typography.sizes.title3};
  margin-right: ${theme.spacing.sm};
`;

const JobPeriod = styled.span`
  background: rgba(74, 157, 255, 0.1);
  padding: ${theme.spacing.xs} ${theme.spacing.sm};
  border-radius: ${theme.borderRadius.pill};
  font-family: 'Fira Code', monospace;
  font-size: ${theme.typography.sizes.caption};
  color: ${theme.colors.primary};
  letter-spacing: 0.5px;
  border: 1px solid rgba(74, 157, 255, 0.2);
`;

const JobLocation = styled.span`
  display: flex;
  align-items: center;
  color: ${theme.colors.textSecondary};
  font-size: ${theme.typography.sizes.body};

  &::before {
    content: '';
    display: inline-block;
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background-color: ${theme.colors.secondary};
    margin-right: ${theme.spacing.xs};
    box-shadow: 0 0 10px rgba(106, 142, 255, 0.5);
  }
`;

const SectionHeader = styled.h4`
  font-size: ${theme.typography.sizes.title3};
  margin-top: ${theme.spacing.xl};
  margin-bottom: ${theme.spacing.md};
  color: ${theme.colors.text};
  display: flex;
  align-items: center;

  &::before {
    content: '//';
    color: ${theme.colors.primary};
    margin-right: ${theme.spacing.sm};
    font-family: 'Fira Code', monospace;
    opacity: 0.8;
  }
`;

const KpiMetricsContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: ${theme.spacing.lg};
  margin-top: ${theme.spacing.md};
  margin-bottom: ${theme.spacing.xl};
`;

const TechnologiesList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: ${theme.spacing.sm};
  margin-top: ${theme.spacing.md};
`;

const Technology = styled.span`
  background: rgba(74, 157, 255, 0.1);
  border: 1px solid rgba(74, 157, 255, 0.2);
  padding: ${theme.spacing.xs} ${theme.spacing.sm};
  border-radius: ${theme.borderRadius.pill};
  font-size: ${theme.typography.sizes.caption};
  color: ${theme.colors.textSecondary};
  backdrop-filter: blur(5px);
  transition: all 0.3s ease;

  &:hover {
    background: rgba(74, 157, 255, 0.2);
    color: ${theme.colors.text};
    transform: translateY(-2px);
    box-shadow: 0 5px 10px rgba(0, 0, 0, 0.1);
  }

  &::before {
    content: '#';
    color: ${theme.colors.primary};
    margin-right: 4px;
  }
`;

// Fixed achievement code - using TypeScript
const generateAchievementCode = (achievement: string) => {
  return `/**
 * Key Achievement 
 */
interface Impact {
  businessValue: string;
  userSatisfaction: string;
  teamEfficiency: string;
}

class Achievement {
  private readonly _impact: string;
  private readonly _date: Date;
  private _metrics?: Impact;

  constructor(impact: string) {
    this._impact = impact;
    this._date = new Date();
  }

  public calculateMetrics(): Impact {
    // Analysis shows significant improvements
    return {
      businessValue: 'High',
      userSatisfaction: '95%',
      teamEfficiency: 'Increased by 40%'
    };
  }

  public logAchievement(): void {
    console.log(\`Achievement unlocked: \${this._impact}\`);
  }
}

// Document the achievement
const keyResult = new Achievement(\`${achievement}\`);
keyResult.logAchievement();`;
};

const Experience: React.FC<ExperienceProps> = ({ experience }: ExperienceProps) => {
  const [activeIndex, setActiveIndex] = useState(0);
  const [selectedJob, setSelectedJob] = useState<WorkExperience>(experience[0]);

  useEffect(() => {
    setSelectedJob(experience[activeIndex]);
  }, [activeIndex, experience]);

  // Map achievement to KPI metrics with fixed icons and colors
  const getKpiMetricsFromAchievements = (job: WorkExperience) => {
    if (!job.achievements || job.achievements.length === 0) return [];

    // Define fixed KPI metrics with consistent naming and coloring
    const kpiData = job.achievements.map((achievement, index) => {
      const text = achievement.toLowerCase();

      // Fixed KPI metric configurations
      const metricTypes = [
        {
          icon: 'clock',
          label: 'Time Reduction',
          accentColor: '#4A9DFF'
        },
        {
          icon: 'users',
          label: 'User Growth',
          accentColor: '#6C8EFF'
        },
        {
          icon: 'chart',
          label: 'Efficiency Increase',
          accentColor: '#50E3C2'
        }
      ];

      // Determine value from achievement text
      let value = '40%';
      if (text.includes('400%')) value = '400%';
      else if (text.includes('35%')) value = '35%';
      else if (text.includes('92%')) value = '92%';
      else if (text.includes('65%')) value = '65%';
      else if (text.includes('15k')) value = '15K';
      else if (text.includes('25%')) value = '25%';

      // Use fixed metric type based on index (ensures consistency)
      const metricType = metricTypes[index % metricTypes.length];

      return {
        value,
        label: metricType.label,
        icon: metricType.icon,
        accentColor: metricType.accentColor,
        delay: index
      };
    });

    return kpiData.slice(0, 3); // Limit to 3 metrics for consistent display
  };

  // Get the most impressive achievement for code display
  const getKeyAchievement = (job: WorkExperience) => {
    if (!job.achievements || job.achievements.length === 0) return '';

    // Simple heuristic - get achievement with most impressive numbers
    const achievement = job.achievements.find(a => 
      a.includes('400') || a.includes('40%') || a.includes('15K')
    );

    return achievement || job.achievements[0];
  };

  return (
    <ExperienceWrapper id="experience">
      <ExperienceContainer>
        <SectionTitle>
          <Heading level={2} size="title1" gradient withAccent>
            Professional Journey
          </Heading>
        </SectionTitle>
        <ExperienceContent>
          <TimelineContainer>
            <Timeline 
              items={experience.map(job => ({
                period: job.period,
                title: job.title,
                company: job.company,
                location: job.location
              }))}
              activeIndex={activeIndex}
              setActiveIndex={setActiveIndex}
            />
          </TimelineContainer>

          <DetailContainer>
            <JobHeader>
              <JobTitle>{selectedJob.title}</JobTitle>
              <JobDetails>
                <JobCompany>{selectedJob.company}</JobCompany>
                <JobLocation>{selectedJob.location}</JobLocation>
                <JobPeriod>{selectedJob.period}</JobPeriod>
              </JobDetails>
            </JobHeader>

            {selectedJob.achievements && selectedJob.achievements.length > 0 && (
              <>
                <SectionHeader>Key Performance Indicators</SectionHeader>
                <KpiMetricsContainer>
                  {getKpiMetricsFromAchievements(selectedJob).map((metric, index) => (
                    <KpiMetric
                      key={index}
                      value={metric.value}
                      label={metric.label}
                      icon={metric.icon}
                      accentColor={metric.accentColor}
                      delay={metric.delay}
                    />
                  ))}
                </KpiMetricsContainer>
              </>
            )}

            <SectionHeader>Achievement Spotlight</SectionHeader>
            <CodeBlock 
              code={generateAchievementCode(getKeyAchievement(selectedJob))}
              language="typescript"
              title={`achievement.ts`}
            />

            {selectedJob.technologies && selectedJob.technologies.length > 0 && (
              <>
                <SectionHeader>Technologies</SectionHeader>
                <TechnologiesList>
                  {selectedJob.technologies.map((tech, index) => (
                    <Technology key={index}>{tech}</Technology>
                  ))}
                </TechnologiesList>
              </>
            )}
          </DetailContainer>
        </ExperienceContent>
      </ExperienceContainer>
    </ExperienceWrapper>
  );
};

export default Experience;
