import React, { useState, useEffect } from 'react';
import styled, { keyframes } from 'styled-components';
import { theme } from '../../styles/theme';

interface CodeBlockProps {
  code: string;
  language?: 'javascript' | 'typescript' | 'jsx' | 'python';
  title?: string;
  autoType?: boolean;
  typingSpeed?: number;
}

// Animation keyframes
const blink = keyframes`
  0%, 100% { opacity: 1; }
  50% { opacity: 0; }
`;

const slideDown = keyframes`
  from {
    opacity: 0;
    transform: translateY(-20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

// Code editor-like container
const CodeContainer = styled.div`
  background-color: #1e1e1e;
  border-radius: ${theme.borderRadius.md};
  overflow: hidden;
  box-shadow: 0 10px 30px rgba(0, 0, 0, 0.3);
  margin: ${theme.spacing.lg} 0;
  font-family: 'Fira Code', Consolas, Monaco, 'Andale Mono', 'Ubuntu Mono', monospace;
  animation: ${slideDown} 0.5s ease-out forwards;
  border: 1px solid ${theme.colors.glassBorder};
`;

const CodeHeader = styled.div`
  background-color: #2d2d2d;
  padding: ${theme.spacing.sm} ${theme.spacing.md};
  display: flex;
  justify-content: space-between;
  align-items: center;
  border-bottom: 1px solid #3e3e3e;
`;

const CodeTitle = styled.div`
  color: #e0e0e0;
  font-size: ${theme.typography.sizes.caption};
  display: flex;
  align-items: center;

  &::before {
    content: '';
    display: inline-block;
    width: 12px;
    height: 12px;
    border-radius: 50%;
    background-color: ${theme.colors.primary};
    margin-right: ${theme.spacing.sm};
  }
`;

const WindowControls = styled.div`
  display: flex;
  align-items: center;
`;

const WindowButton = styled.div<{ color: string }>`
  width: 12px;
  height: 12px;
  border-radius: 50%;
  background-color: ${props => props.color};
  margin-left: 8px;
`;

const CodeContent = styled.pre`
  margin: 0;
  padding: ${theme.spacing.md} ${theme.spacing.lg};
  overflow-x: auto;
  color: #e0e0e0;
  font-size: ${theme.typography.sizes.caption};
  line-height: 1.5;

  &::-webkit-scrollbar {
    width: 8px;
    height: 8px;
  }

  &::-webkit-scrollbar-track {
    background: #2d2d2d;
  }

  &::-webkit-scrollbar-thumb {
    background: #3e3e3e;
    border-radius: ${theme.borderRadius.sm};
  }
`;

const Cursor = styled.span`
  display: inline-block;
  width: 2px;
  height: 1em;
  background-color: #e0e0e0;
  margin-left: 2px;
  animation: ${blink} 1s infinite;
  vertical-align: middle;
`;

// Syntax highlighting functions (simplified version)
const highlightJavaScript = (code: string) => {
  // Replace keywords with colored spans
  let highlighted = code
    .replace(/\b(const|let|var|function|return|if|else|for|while|class|import|export|from|async|await)\b/g, '<span style="color: #569CD6;">$1</span>')
    .replace(/\b(true|false|null|undefined|this)\b/g, '<span style="color: #569CD6;">$1</span>')
    .replace(/(['"])(?:(?=(\\?))\2.)*?\1/g, '<span style="color: #CE9178;">$&</span>')
    .replace(/\b(\d+)\b/g, '<span style="color: #B5CEA8;">$1</span>')
    .replace(/\/\/.*/g, '<span style="color: #6A9955;">$&</span>')
    .replace(/\/\*[\s\S]*?\*\//g, '<span style="color: #6A9955;">$&</span>');

  return highlighted;
};

const highlightTS = (code: string) => {
  let highlighted = highlightJavaScript(code);
  // Add TypeScript specific highlighting
  highlighted = highlighted
    .replace(/\b(interface|type|extends|implements|any|void|string|number|boolean)\b/g, '<span style="color: #4EC9B0;">$1</span>');

  return highlighted;
};

const highlightPython = (code: string) => {
  // Replace Python keywords with colored spans
  let highlighted = code
    .replace(/\b(def|class|import|from|as|return|if|elif|else|for|while|in|with|True|False|None)\b/g, '<span style="color: #569CD6;">$1</span>')
    .replace(/(['"])(?:(?=(\\?))\2.)*?\1/g, '<span style="color: #CE9178;">$&</span>')
    .replace(/\b(\d+)\b/g, '<span style="color: #B5CEA8;">$1</span>')
    .replace(/#.*/g, '<span style="color: #6A9955;">$&</span>');

  return highlighted;
};

const CodeBlock: React.FC<CodeBlockProps> = ({ 
  code, 
  language = 'javascript', 
  title = 'achievement.js',
  autoType = true,
  typingSpeed = 30
}) => {
  const [displayedCode, setDisplayedCode] = useState('');
  const [isTyping, setIsTyping] = useState(autoType);

  useEffect(() => {
    if (autoType) {
      let i = 0;
      setDisplayedCode('');

      const typingInterval = setInterval(() => {
        if (i < code.length) {
          setDisplayedCode(prev => prev + code.charAt(i));
          i++;
        } else {
          setIsTyping(false);
          clearInterval(typingInterval);
        }
      }, typingSpeed);

      return () => clearInterval(typingInterval);
    } else {
      setDisplayedCode(code);
      setIsTyping(false);
    }
  }, [code, autoType, typingSpeed]);

  const getHighlightedCode = () => {
    let highlighted = displayedCode;

    switch (language) {
      case 'javascript':
        highlighted = highlightJavaScript(displayedCode);
        break;
      case 'typescript':
        highlighted = highlightTS(displayedCode);
        break;
      case 'jsx':
        highlighted = highlightJavaScript(displayedCode);
        break;
      case 'python':
        highlighted = highlightPython(displayedCode);
        break;
    }

    return { __html: highlighted };
  };

  // Language-specific file icon
  const getLanguageIcon = () => {
    switch (language) {
      case 'javascript':
        return '.js';
      case 'typescript':
        return '.ts';
      case 'jsx':
        return '.jsx';
      case 'python':
        return '.py';
      default:
        return '';
    }
  };

  return (
    <CodeContainer>
      <CodeHeader>
        <CodeTitle>{title || `achievement${getLanguageIcon()}`}</CodeTitle>
        <WindowControls>
          <WindowButton color="#FF5F56" />
          <WindowButton color="#FFBD2E" />
          <WindowButton color="#27C93F" />
        </WindowControls>
      </CodeHeader>
      <CodeContent>
        <div dangerouslySetInnerHTML={getHighlightedCode()} />
        {isTyping && <Cursor />}
      </CodeContent>
    </CodeContainer>
  );
};

export default CodeBlock;
