import { PortfolioData } from '../types';

export const portfolioData: PortfolioData = {
  personalInfo: {
    name: "Anup Shrestha",
    title: "Product Manager",
    tagline: "Driving innovation from 0 to 1 with data-first, customer-centric solutions.",
    email: "anupsth23@gmail.com",
    location: "Toronto, Ontario"
  },
  summary: "Execution-driven Product Manager with a data-first, customer-centric approach. Skilled in 0→1 product development, automation, and agile execution. Thrives in fast-paced startups, balancing speed, strategy, and cross-functional collaboration to drive growth.",
  experience: [
    {
      title: "Product Manager",
      company: "TwinsAI",
      location: "USA",
      period: "Jan 2025 - Present",
      responsibilities: [
        "Partnered directly with founders to refine product vision, strategy, and roadmap, ensuring every release aligned with long-term business goals.",
        "Led the development of an AI-powered sales automation tool, reducing outbound outreach time by 40% and improving conversion rates from 20 to 400 times.",
        "Defined and prioritized a customer-driven backlog, addressing high-impact feature requests."
      ]
    },
    {
      title: "Product Manager",
      company: "Rumsan Associates",
      location: "Nepal",
      period: "December 2021 - March 2023",
      responsibilities: [
        "Led cross-functional teams in delivering 16 SaaS projects from idea phase from the client, development, testing, launch and support of the application.",
        "Designed and launched a blockchain-based financial product from scratch, securing +4 government partnerships and acquiring 15K+ users.",
        "Built and managed 0→1 product features, making iterative improvements based on real-world customer feedback and market validation.",
        "Led cross-functional efforts to scale MVP adoption, balancing short-term pivots with long-term strategic goals."
      ]
    },
    {
      title: "Business Analyst",
      company: "Rumsan Associates",
      location: "Nepal",
      period: "November 2018 - November 2021",
      responsibilities: [
        "Identified critical UX bottlenecks, leading to a redesign that reduced churn by 25%.",
        "Worked with founders and stakeholders to align product development with strategic growth levers, ensuring sustainable scaling.",
        "Acted as a liaison between the business team and technical team to ensure, the developers would understand what was required by the business team."
      ]
    }
  ]
};
