import React from 'react';
import styled from 'styled-components';
import Section from '../ui/Section';
import Container from '../ui/Container';
import Heading from '../ui/Heading';
import { theme } from '../../styles/theme';

interface SummaryProps {
  summary: string;
  vision?: string;
}

const SummaryWrapper = styled(Section)`
  background: linear-gradient(
    180deg,
    ${theme.colors.background} 0%,
    ${theme.colors.backgroundAlt} 100%
  );
`;

const ContentContainer = styled(Container)`
  display: flex;
  flex-direction: column;
  align-items: center;

  @media (min-width: ${theme.breakpoints.lg}) {
    flex-direction: row;
    align-items: flex-start;
    gap: ${theme.spacing.xxxl};
  }
`;

const SummaryContent = styled.div`
  flex: 1;
  margin-bottom: ${theme.spacing.xl};

  @media (min-width: ${theme.breakpoints.lg}) {
    margin-bottom: 0;
  }
`;

const VisionContent = styled.div`
  flex: 1;
  position: relative;
`;

const SummaryText = styled.p`
  font-size: ${theme.typography.sizes.body};
  line-height: ${theme.typography.lineHeights.relaxed};
  color: ${theme.colors.textSecondary};
  margin-bottom: ${theme.spacing.lg};
  position: relative;

  &::before {
    content: '';
    position: absolute;
    top: -${theme.spacing.md};
    left: 0;
    width: 60px;
    height: 4px;
    background: ${theme.colors.gradientPrimary};
    border-radius: 2px;
  }
`;

const VisionText = styled.p`
  font-size: ${theme.typography.sizes.body};
  line-height: ${theme.typography.lineHeights.relaxed};
  color: ${theme.colors.textSecondary};
  position: relative;
  padding: ${theme.spacing.xl};
  border-radius: ${theme.borderRadius.lg};
  background: ${theme.colors.glass};
  backdrop-filter: blur(10px);
  border: 1px solid ${theme.colors.glassBorder};

  /* Decorative corner accent */
  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    width: 80px;
    height: 80px;
    background: ${theme.colors.gradientSecondary};
    opacity: 0.3;
    filter: blur(30px);
    border-radius: 50%;
    transform: translate(25%, -25%);
  }

  &::after {
    content: '';
    position: absolute;
    bottom: -10px;
    left: 20px;
    width: calc(100% - 40px);
    height: 20px;
    background: ${theme.colors.glass};
    backdrop-filter: blur(10px);
    border: 1px solid ${theme.colors.glassBorder};
    border-radius: ${theme.borderRadius.sm};
    z-index: -1;
  }
`;

const QuoteIcon = styled.span`
  position: absolute;
  top: -20px;
  left: 30px;
  width: 40px;
  height: 40px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  background: ${theme.colors.primary};
  color: white;
  font-size: 24px;
  z-index: 2;
`;

const Summary: React.FC<SummaryProps> = ({ summary, vision }: SummaryProps) => {
  return (
    <SummaryWrapper id="summary">
      <ContentContainer>
        <SummaryContent>
          <Heading level={2} size="headline" marginBottom="large">
            Summary
          </Heading>
          <SummaryText>
            {summary}
          </SummaryText>
        </SummaryContent>

        {vision && (
          <VisionContent>
            <Heading level={2} size="headline" marginBottom="large">
              Vision
            </Heading>
            <VisionText>
              <QuoteIcon>"</QuoteIcon>
              {vision}
            </VisionText>
          </VisionContent>
        )}
      </ContentContainer>
    </SummaryWrapper>
  );
};

export default Summary;
