import React from 'react';
import styled, { keyframes } from 'styled-components';
import Section from '../ui/Section';
import Container from '../ui/Container';
import Heading from '../ui/Heading';
import { theme } from '../../styles/theme';

interface SummaryProps {
  summary: string;
  vision?: string;
}

// Animation for AI-themed elements
const pulse = keyframes`
  0% { opacity: 0.6; }
  50% { opacity: 1; }
  100% { opacity: 0.6; }
`;

const float = keyframes`
  0% { transform: translateY(0); }
  50% { transform: translateY(-10px); }
  100% { transform: translateY(0); }
`;

// Animated grid background
const gridAnimation = keyframes`
  0% { background-position: 0px 0px; }
  100% { background-position: 100px 100px; }
`;

const SummaryWrapper = styled(Section)`
  background: linear-gradient(
    160deg,
    ${theme.colors.background} 0%,
    ${theme.colors.backgroundAlt} 80%
  );
  position: relative;
  overflow: hidden;

  /* AI-themed grid pattern */
  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: 
      linear-gradient(${theme.colors.aiGrid} 1px, transparent 1px),
      linear-gradient(90deg, ${theme.colors.aiGrid} 1px, transparent 1px);
    background-size: 50px 50px;
    opacity: 0.4;
    z-index: 0;
    animation: ${gridAnimation} 100s linear infinite;
  }
`;

const ContentContainer = styled(Container)`
  display: flex;
  flex-direction: column;
  align-items: center;
  position: relative;
  z-index: 1;
  padding: ${theme.spacing.xxxl} ${theme.spacing.md};

  @media (min-width: ${theme.breakpoints.lg}) {
    flex-direction: row;
    align-items: stretch;
    gap: ${theme.spacing.xxl};
  }
`;

const SummaryContent = styled.div`
  flex: 1;
  margin-bottom: ${theme.spacing.xxl};
  position: relative;

  @media (min-width: ${theme.breakpoints.lg}) {
    margin-bottom: 0;
  }
`;

const VisionContent = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  position: relative;
`;

const SectionHeading = styled(Heading)`
  position: relative;
  overflow: hidden;
  display: inline-block;

  &::after {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    height: 4px;
    width: 60px;
    background: ${theme.colors.gradientPrimary};
    border-radius: ${theme.borderRadius.sm};
  }
`;

const AIBlob = styled.div`
  position: absolute;
  border-radius: 50%;
  filter: blur(50px);
  z-index: -1;

  &.primary {
    top: -100px;
    right: -50px;
    width: 300px;
    height: 300px;
    background: ${theme.colors.aiGlow};
    opacity: 0.3;
    animation: ${pulse} 10s infinite, ${float} 15s infinite;
  }

  &.secondary {
    bottom: -100px;
    left: -100px;
    width: 400px;
    height: 400px;
    background: ${theme.colors.aiAccent};
    opacity: 0.2;
    animation: ${pulse} 12s infinite, ${float} 20s infinite;
  }
`;

const SummaryText = styled.p`
  font-size: ${theme.typography.sizes.body};
  line-height: ${theme.typography.lineHeights.relaxed};
  color: ${theme.colors.textSecondary};
  margin-top: ${theme.spacing.lg};
  padding: ${theme.spacing.lg};
  background: ${theme.colors.glass};
  backdrop-filter: blur(15px);
  border-radius: ${theme.borderRadius.lg};
  border: 1px solid ${theme.colors.glassBorder};

  /* Subtle highlight on the left side */
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 4px;
    height: 100%;
    background: ${theme.colors.gradientPrimary};
    border-radius: ${theme.borderRadius.sm};
  }
`;

const VisionCard = styled.div`
  position: relative;
  padding: ${theme.spacing.xl};
  background: ${theme.colors.aiSurface};
  border-radius: ${theme.borderRadius.lg};
  border: 1px solid ${theme.colors.glassBorder};
  backdrop-filter: blur(20px);
  margin-top: ${theme.spacing.lg};
  height: 100%;
  display: flex;
  flex-direction: column;
  justify-content: center;
  box-shadow: ${theme.shadows.aiGlow};

  /* Accent lighting */
  &::before {
    content: '';
    position: absolute;
    top: -20px;
    right: -20px;
    width: 150px;
    height: 150px;
    background: ${theme.colors.aiAccent};
    filter: blur(60px);
    border-radius: 50%;
    opacity: 0.5;
    z-index: -1;
    animation: ${pulse} 8s infinite ease-in-out;
  }
`;

const VisionText = styled.p`
  font-size: ${theme.typography.sizes.body};
  line-height: ${theme.typography.lineHeights.relaxed};
  color: ${theme.colors.textSecondary};
  margin: 0;
`;

const QuoteIcon = styled.div`
  position: absolute;
  top: -25px;
  left: ${theme.spacing.lg};
  width: 50px;
  height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  border-radius: 50%;
  background: ${theme.colors.primary};
  color: white;
  font-size: 28px;
  z-index: 2;
  box-shadow: ${theme.shadows.aiGlow};

  &::after {
    content: '"';
  }
`;

const Summary: React.FC<SummaryProps> = ({ summary, vision }: SummaryProps) => {
  return (
    <SummaryWrapper id="summary">
      <AIBlob className="primary" />
      <AIBlob className="secondary" />

      <ContentContainer>
        <SummaryContent>
          <SectionHeading level={2} size="headline" marginBottom="large">
            Summary
          </SectionHeading>
          <SummaryText>
            {summary}
          </SummaryText>
        </SummaryContent>

        {vision && (
          <VisionContent>
            <SectionHeading level={2} size="headline" marginBottom="large">
              Vision
            </SectionHeading>
            <VisionCard>
              <QuoteIcon />
              <VisionText>
                {vision}
              </VisionText>
            </VisionCard>
          </VisionContent>
        )}
      </ContentContainer>
    </SummaryWrapper>
  );
};

export default Summary;
