import React from 'react';
import styled, { keyframes } from 'styled-components';
import { theme } from '../../styles/theme';

interface KpiMetricProps {
  value: string;
  label: string;
  icon?: string;
  prefix?: string;
  suffix?: string;
  accentColor?: string;
  delay?: number;
}

const fadeIn = keyframes`
  from { opacity: 0; transform: translateY(20px); }
  to { opacity: 1; transform: translateY(0); }
`;

const float = keyframes`
  0% { transform: translateY(0); }
  50% { transform: translateY(-5px); }
  100% { transform: translateY(0); }
`;

const highlight = keyframes`
  0% { opacity: 0.3; }
  50% { opacity: 1; }
  100% { opacity: 0.3; }
`;

const MetricContainer = styled.div<{ accentColor: string; delay: number }>`
  display: flex;
  flex-direction: column;
  padding: ${theme.spacing.lg};
  border-radius: ${theme.borderRadius.lg};
  background: ${theme.colors.glass};
  backdrop-filter: blur(10px);
  border: 1px solid ${theme.colors.glassBorder};
  position: relative;
  overflow: hidden;
  transition: all ${theme.transitions.normal};
  animation: ${fadeIn} 0.6s forwards;
  animation-delay: ${props => `${props.delay}s`};
  opacity: 0;

  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);

    &::before {
      opacity: 0.8;
      transform: scale(1.1);
    }
  }

  /* Accent glow */
  &::before {
    content: '';
    position: absolute;
    top: 0;
    right: 0;
    width: 100px;
    height: 100px;
    background: ${props => props.accentColor || theme.colors.primary};
    filter: blur(40px);
    opacity: 0.5;
    border-radius: 50%;
    transition: all 0.5s ease;
    z-index: 0;
  }

  /* Tech line decoration */
  &::after {
    content: '';
    position: absolute;
    bottom: 10px;
    left: 10px;
    width: 30%;
    height: 1px;
    background: linear-gradient(
      to right,
      ${props => props.accentColor || theme.colors.primary},
      transparent
    );
  }
`;

const MetricValue = styled.div<{ accentColor: string }>`
  font-size: 2.5rem;
  font-weight: ${theme.typography.fontWeights.bold};
  margin-bottom: ${theme.spacing.sm};
  color: ${theme.colors.text};
  position: relative;
  z-index: 1;
  display: flex;
  align-items: baseline;
  animation: ${float} 4s ease-in-out infinite;
  background: linear-gradient(135deg, #fff 0%, ${props => props.accentColor || theme.colors.primary} 100%);
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
`;

const MetricLabel = styled.div`
  font-size: ${theme.typography.sizes.body};
  color: ${theme.colors.textSecondary};
  max-width: 220px;
  position: relative;
  z-index: 1;
`;

const IconContainer = styled.div<{ accentColor: string }>`
  position: absolute;
  top: ${theme.spacing.md};
  right: ${theme.spacing.md};
  width: 32px;
  height: 32px;
  display: flex;
  align-items: center;
  justify-content: center;
  color: ${props => props.accentColor || theme.colors.primary};
  border-radius: 50%;
  opacity: 0.7;
  z-index: 2;
  animation: ${highlight} 3s infinite;
`;

const Prefix = styled.span`
  font-size: ${theme.typography.sizes.title3};
  margin-right: ${theme.spacing.xs};
  opacity: 0.7;
`;

const Suffix = styled.span`
  font-size: ${theme.typography.sizes.title3};
  margin-left: ${theme.spacing.xs};
  opacity: 0.7;
`;

// Icons as React components using SVG
const Icons = {
  trending: (color: string) => (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M3 17L9 11L13 15L21 7" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M14 7H21V14" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    </svg>
  ),
  users: (color: string) => (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M17 21V19C17 16.7909 15.2091 15 13 15H5C2.79086 15 1 16.7909 1 19V21" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M9 11C11.2091 11 13 9.20914 13 7C13 4.79086 11.2091 3 9 3C6.79086 3 5 4.79086 5 7C5 9.20914 6.79086 11 9 11Z" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M23 21V19C22.9986 17.1771 21.7079 15.5857 20 15.13" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M16 3.13C17.7105 3.58317 19.0053 5.17786 19.0053 7.005C19.0053 8.83214 17.7105 10.4268 16 10.88" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    </svg>
  ),
  clock: (color: string) => (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
      <circle cx="12" cy="12" r="10" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M12 6V12L16 14" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    </svg>
  ),
  lightbulb: (color: string) => (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M9.66347 17H14.3365" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M12 3V4" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M18.3639 5.63604L17.6568 6.34315" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M21 12H20" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M18.3639 18.364L17.6568 17.6569" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M5.63604 18.364L6.34315 17.6569" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M4 12H3" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M5.63604 5.63604L6.34315 6.34315" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M12 19V21" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path fillRule="evenodd" clipRule="evenodd" d="M12 15C14.7614 15 17 12.7614 17 10C17 7.23858 14.7614 5 12 5C9.23858 5 7 7.23858 7 10C7 12.7614 9.23858 15 12 15Z" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    </svg>
  ),
  chart: (color: string) => (
    <svg width="24" height="24" viewBox="0 0 24 24" fill="none" xmlns="http://www.w3.org/2000/svg">
      <path d="M18 20V10" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M12 20V4" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
      <path d="M6 20V14" stroke={color} strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
    </svg>
  )
};

const KpiMetric: React.FC<KpiMetricProps> = ({ 
  value, 
  label, 
  icon = 'trending',
  prefix,
  suffix,
  accentColor = theme.colors.primary,
  delay = 0
}) => {
  const renderIcon = () => {
    if (icon in Icons) {
      return Icons[icon as keyof typeof Icons](accentColor);
    }
    return null;
  };

  return (
    <MetricContainer accentColor={accentColor} delay={delay}>
      <IconContainer accentColor={accentColor}>
        {renderIcon()}
      </IconContainer>
      <MetricValue accentColor={accentColor}>
        {prefix && <Prefix>{prefix}</Prefix>}
        {value}
        {suffix && <Suffix>{suffix}</Suffix>}
      </MetricValue>
      <MetricLabel>{label}</MetricLabel>
    </MetricContainer>
  );
};

export default KpiMetric;
